var exec = require('child_process').exec;

exports.varLoggit = function(varname) {
    return varname;
}

exports.consoleLoggit = function(consoletext) {
    console.log("$ " + consoletext);
}

exports.rawConsoleLoggit = function(rawtext) {
    console.log(rawtext);
}

exports.echoLoggit = function(echotext) {
    console.log("$ " + echotext);
}

exports.rawEchoLoggit = function(rawechotext) {
    console.log(rawechotext);
}

exports.loggitInit = function() {
    console.log("+--------------------------------------+");
    console.log("------------                ------------");
    console.log("---------- Loggit Initialized ----------");
    console.log("------------                ------------");
    console.log("+--------------------------------------+");
}

exports.loggitCommand = function(cmdString) {
    return exec(
        cmdString
    );
}